// client/src/pages/HomePage.js
import React, { useEffect } from 'react';
import '../styles/Homepage.css';

const HomePage = () => {
  useEffect(() => {
    const videoElement = document.querySelector('video');
    if (videoElement) {
      videoElement.play();  // Ensure the video starts playing immediately
    }
  }, []);

  return (
    <div className="navbar">
      <button className="nav-button" onClick={() => window.open('/User', '_blank')}>User</button>
      <button className="nav-button" onClick={() => window.open('/journalist', '_blank')}>Journalist</button>
      <button className="nav-button">Writer</button>

      {/* Video section */}
      <div className="video-container">
        <video width="100%" height="100%" autoPlay muted controls>
          <source src="../styles/E_Newspaper.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>
    </div>
  );
};

export default HomePage;
